import React from 'react';
import { useState } from 'react';
import Axios from 'axios';


const HomeForm = () => {
    const url ="http://localhost:3000/?"
    const [data, setData] = useState({
        name: "",
        date: "",
        idUser: ""
    })

    function submit(e){
        e.preventDefault();
        Axios.post(url,{
            name: data.name,
            date: data.date,
            idUser: data.idUser
            })
            .then(res=>{

            })
    }

    function handle(e){
        const newdata={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
    }
    return (
        <div>
            <form onSubmit={(e) => submit(e)}>
                <input onChange={(e)=>handle(e)} id="name" value={data.name}
                placeholder='Name' type="text" />
                <input onChange={(e)=>handle(e)} id="date" value={data.date}
                placeholder='date' type="date" />
                <input onChange={(e)=>handle(e)} id="idUser" value={data.idUser}
                placeholder='idUser' type="number" />
                <button>Poster</button>
            </form>
        </div>
    );
};

export default HomeForm;
